mapper.select:
select user_id, email, password, username, provider_type, role_type
from USER
where email = #{email}

select user_id, email, password, username, provider_type, role_type
from USER
where user_id = #{userId}

mapper.update:
update USER
set username = #{username}
where user_id = #{userId}

update USER
set password = #{password}
where user_id = #{userId}
